function calculateSum(arr) {
  let sum = 0;
  for (let i = 0; i < arr.length; i++) {
    sum += arr[i];
  }
  return sum;
}

// 使用reduce方法的现代实现
function calculateSum(arr) {
  return arr.reduce((sum, current) => sum + current, 0);
}
